import React from 'react'
import { View, Text, StyleSheet, Image, ScrollView } from 'react-native'
import { useCategoryId } from '../hooks/useCategoryId'
import { ListOfServices } from './../components/ListOfServices/index'
import { CategoryItem } from '../components/CategoryItem'
import { categoryIcons } from '../data/categories'

export const Category = ({ match }) => {
  // const [category, setCategory] = useState({})
  const categoryId = match.params.id
  const { category, loading } = useCategoryId(categoryId)

  //   useEffect(() => {
  //     setCategory(category)
  //   }, [categoryId])

  if (loading) {
    return <Text>Cargando...</Text>
  }

  return (
    <ScrollView showsVerticalScrollIndicator={false}>
      <View style={styles.container}>
        <View style={{ marginTop: 10 }}>
          <CategoryItem
            showNameAsTitle
            name={category?.name}
            color={category?.colorRGBA}
            id={category?.id}
            icon={categoryIcons[category?.icon]}
            key={category?.id}
          />
          <View style={{ marginVertical: -30, marginBottom: 0 }}>
            <ListOfServices title='' categoryId={categoryId} />
          </View>

        </View>
      </View>
    </ScrollView>

  )
}

const styles = StyleSheet.create({
  container: {
    marginTop: 20
  }
})
