import React, { useState, useEffect } from 'react'
import { StyleSheet, Text, View, TouchableWithoutFeedback } from 'react-native'
import { ServiceItem } from '../ServiceItem'
import { Link } from 'react-router-native'
import myServices from '../../data/services'
import theme from './../../styles/theme'
import { db } from '../../../database/firebase'

export const ListOfServices = ({ title = 'Servicios', categoryId, filterByName }) => {
  const [services, setServices] = useState([])

  useEffect(() => {
    console.log('here')
    const services = []
    db.collection('services').onSnapshot((querySnapshot) => {
      querySnapshot.docs.forEach(doc => {
        const service = doc.data()
        service.user.get().then(snap => {
          service.user = snap.data()
          console.log({ service })
          services.push({ ...service, id: doc.id })
        })
      })
    })
    setServices(myServices)
  }, [])

  useEffect(() => {
    if (categoryId) {
      const filteredServices = myServices.filter((service) => Number(service.category.id) === Number(categoryId))
      setServices(filteredServices)
    }
  }, [categoryId])

  useEffect(() => {
    if (filterByName) {
      const filteredServices = myServices.filter((service) => service.name.toLowerCase().includes(filterByName.toLowerCase()))
      setServices(filteredServices)
    }
  }, [filterByName])

  return (
    <View style={styles.container}>
      <Text style={styles.title}>{title}</Text>
      {
          services.map((service) => (
            <Link key={service.id} to={`/service/${service.id}`} component={TouchableWithoutFeedback}>
              <View style={{ marginTop: 20 }}>
                <ServiceItem
                  id={service.id}
                  avatar={service.user.avatar}
                  name={service.name}
                  owner={`${service.user.name} ${service.user.lastName}`}
                  category={service.category}
                  description={service.description}
                />

              </View>
            </Link>
          ))
      }
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    /* paddingVertical: 10 */
    marginTop: 5
  },
  title: {
    ...theme.common.title,
    fontSize: theme.fontSizes.bigTitle,
    color: theme.colors.white,
    marginTop: 10
    /* marginBottom: -20 */
  }
})
