import React from 'react'
import { View, StyleSheet, ScrollView, Text, Pressable } from 'react-native'
import { CategoryItem } from './../components/CategoryItem/index'
import { useHistory } from 'react-router-native'
import { categoryIcons } from '../data/categories'
import theme from './../styles/theme'
import { useCategories } from '../hooks/useCategories'

export const Categories = () => {
  const history = useHistory()
  const { loading, categories } = useCategoriess()
  if (loading) return <Text>'Cargando...'</Text>

  return (
    <ScrollView showsVerticalScrollIndicator={false}>
      <View style={styles.container}>
        <Text style={styles.title}>Categorias</Text>
        <View style={styles.listContainer}>
          {categories.map((category) => {
            return (
              <Pressable key={category.id} onPress={() => history.push(`/category/${category.id}`)}>
                <CategoryItem
                  name={category.name}
                  color={category.color}
                  id={category.id}
                  icon={categoryIcons[category.icon]}
                  key={category.id}
                />
              </Pressable>
            )
          })}
        </View>
        <View style={{ marginTop: 40 }}>
          <View style={styles.card}>
            <Text style={{ ...styles.title, color: theme.colors.background }}>¿Te gusta nuestra app?</Text>
            <Text style={{ ...styles.title, color: theme.colors.background }}>¿Tienes alguna sugerencia?</Text>
            <Text>Envíanos un correo a <Text style={{ color: theme.colors.primary }}>appservicios@gmail.com</Text></Text>
          </View>
        </View>
      </View>
    </ScrollView>

  )
}

const styles = StyleSheet.create({
  container: {
    marginTop: 20
  },
  listContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between'
  },
  flexRow: {
    flexDirection: 'row'
  },
  flexCenter: {
    width: '100%',
    justifyContent: 'center',
    alignItems: 'center'
  },
  title: {
    ...theme.common.title,
    fontSize: theme.fontSizes.bigTitle,
    color: theme.colors.white,
    marginBottom: 10
  },
  card: {
    backgroundColor: theme.colors.white,
    padding: 20,
    borderRadius: 20,
    alignItems: 'center',
    ...theme.common.shadowProp
  }
})
