const theme = {
  borderRadius: {
    primary: 20,
    secondary: 10
  },
  colors: {
    textPrimary: '#172A3A',
    textSecondary: '#586069',
    background: '#172A3A',
    primary: '#09BC82',
    secondary: '#75DDDD',
    lightGreen: '#E4F0F0',
    white: '#F3F5FF',
    purple: '#5271FF',
    red: '#F34B50',
    yellow: '#FFA10C',
    blue: '#05B1FB'
  },
  opacity: {
    light: '25%'
  },
  fontSizes: {
    body: 14,
    subheading: 16,
    title: 18,
    bigTitle: 22,
    small: 12
  },

  fontWeights: {
    bold: '700'
  },
  fonts: {
    main: 'System'
  },
  common: {
    flexRow: {
      flexDirection: 'row',
      alignItems: 'center'
    },
    title: {
      fontWeight: '700',
      fontSize: 18
    },
    shadowProp: {
      shadowColor: '#000',
      shadowOffset: { width: 0, height: 4 },
      shadowOpacity: 0.8,
      shadowRadius: 3
    }
  }
}

export default theme
