import React, { useEffect, useState } from 'react'
import { View, Text, StyleSheet, Image, ScrollView } from 'react-native'
import categories from '../data/categories'
import services from '../data/services'
import { Button } from '../components/common/Button'
import { ListOfServices } from './../components/ListOfServices/index'
import { CategoryItem } from '../components/CategoryItem'

export const Category = ({ match }) => {
  const [category, setCategory] = useState({})
  const [services, setServices] = useState([])
  const categoryId = match.params.id

  useEffect(() => {
    const category = categories.find((category) => Number(category.id) === Number(categoryId))
    /*  const filteredServices = services.filter((service) => Number(service.category) === Number(categoryId)) */
    setCategory(category)
    /* setServices(filteredServices) */
  }, [categoryId])

  return (
    <ScrollView showsVerticalScrollIndicator={false}>
      <View style={styles.container}>
        <View style={{ marginTop: 5 }}>
          <CategoryItem
            showNameAsTitle
            name={category?.name}
            color={category?.color}
            id={category?.id}
            icon={category?.icon}
            key={category?.id}
          />
          <View style={{ marginVertical: -30, marginBottom: 0 }}>
            <ListOfServices title='' categoryId={categoryId} />
          </View>

        </View>
      </View>
    </ScrollView>

  )
}

const styles = StyleSheet.create({
  container: {
    marginTop: 20
  }
})
