import { CategoryItem } from '../CategoryItem'
import { View, Text, StyleSheet, Pressable, Alert } from 'react-native'
import { Button } from '../common/Button'
import { Link, useHistory } from 'react-router-native'
import { categoryIcons } from '../../data/categories'
import React from 'react'
import theme from './../../styles/theme'
import { useCategories } from './../../hooks/useCategories'

export const ListOfCategories = () => {
  const { loading, categories } = useCategories()
  const history = useHistory()

  if (loading) return <Text>'Cargando...'</Text>

  console.log({ categories })

  return (
    <View>
      <View style={styles.headerContainer}>
        <Text style={styles.title}>Categorias</Text>
        <Button text='Ver mÃ¡s...' color={theme.colors.primary} textColor={theme.colors.white} onClick={() => history.push('/categories')} />
      </View>

      <View style={styles.listContainer}>
        {categories.slice(0, 4).map((category) => {
          return (
            <Pressable key={category.id} onPress={() => history.push(`/category/${category.id}`)}>
              <CategoryItem
                name={category.name}
                color={category.colorRGBA}
                id={category.id}
                icon={categoryIcons[category.icon]}
              />
            </Pressable>
          )
        })}
      </View>
    </View>
  )
}

const styles = StyleSheet.create({
  headerContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between',
    marginBottom: 10
  },
  listContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-between'
  },
  title: {
    ...theme.common.title,
    fontSize: theme.fontSizes.bigTitle,
    color: theme.colors.white,
    marginBottom: 10
  }
})
