import React, { useEffect, useState } from 'react'
import { View, Text, StyleSheet, Image, ScrollView } from 'react-native'
import services from '../data/services'
import { Button } from '../components/common/Button'
import { ListOfServices } from './../components/ListOfServices/index'
import theme from './../styles/theme'
import { db } from '../../database/firebase'

export const Service = ({ match }) => {
  const [service, setService] = useState({})
  const serviceId = match.params.id

  const getElementById = async (id) => {
    const dbRef = db.collection('services').doc(id)
    const doc = await dbRef.get()
    const service = doc.data()
    const user = await service.user.get()
    const category = await service.category.get()
    console.log({ service, user, category })
  }

  useEffect(() => {
    getElementById(serviceId)
    const service = services.find((service) => Number(service.id) === Number(serviceId))
    console.log(service)
    setService(service)
  }, [serviceId])

  const owner = `${service?.user?.name} ${service?.user?.lastName}`

  return (
    <ScrollView showsVerticalScrollIndicator={false}>
      <View style={styles.container}>

        <View style={styles.card}>
          <Text style={{ color: service?.category?.hexColor }}>{service?.category?.name}</Text>
          <View style={{ ...styles.flexCenter, marginTop: 20 }}>
            <Image style={styles.avatar} source={{ uri: service?.user?.avatar }} />
            <Text style={{ ...styles.owner, marginTop: 8 }}>{owner}</Text>
            <Text style={styles.service}>{service?.name}</Text>
          </View>
          <View>
            <Text style={styles.information}>Informaci칩n</Text>
            <Text style={styles.description}>{service?.description}</Text>
          </View>

          <View>
            <Text style={styles.information}>Ubicaci칩n</Text>
            <Text style={styles.description}>Margarita, Pampatar.</Text>
          </View>

          <View style={{ marginTop: 20 }}>
            <Text style={styles.textMessage}>Al solicitar el servicio, recibir치s un correo electr칩nico con los datos de contacto.</Text>
            <View style={{ marginTop: 10 }}>
              <Button text='Solicitar Servicio' color={service?.category?.hexColor} />
            </View>
          </View>
        </View>
        <View style={{ paddingVertical: 20 }}>
          <ListOfServices title='Otros servicios similares' categoryId={service?.category?.id} />
        </View>
      </View>
    </ScrollView>

  )
}

const styles = StyleSheet.create({
  container: {
    marginTop: 10
  },
  card: {
    backgroundColor: theme.colors.white,
    padding: 20,
    borderRadius: 20
  },
  flexRow: {
    flexDirection: 'row'
  },
  flexCenter: {
    width: '100%',
    justifyContent: 'center',
    alignItems: 'center'
  },
  avatar: {
    width: 70,
    height: 70,
    borderRadius: 10
  },
  service: {
    fontWeight: '500',
    color: theme.colors.background
  },
  description: {
    paddingVertical: 8,
    color: theme.colors.background
  },
  owner: {
    fontSize: theme.fontSizes.title,
    fontWeight: 'bold',
    color: theme.colors.background
  },
  information: {
    fontWeight: 'bold',
    fontSize: theme.fontSizes.body,
    color: theme.colors.background
  },
  textMessage: {
    fontSize: theme.fontSizes.small,
    color: '#C2C2C2'
  }
})
